import{_ as e,c as t,o as a,a as o}from"./app.af31bdc2.js";const u=JSON.parse('{"title":"pxt-pyconv 手册页","description":"","frontmatter":{},"headers":[{"level":2,"title":"操作","slug":"操作","link":"#操作","children":[]},{"level":2,"title":"python 在哪里?","slug":"python-在哪里","link":"#python-在哪里","children":[]},{"level":2,"title":"局限性","slug":"局限性","link":"#局限性","children":[]},{"level":2,"title":"另请参见","slug":"另请参见","link":"#另请参见","children":[]}],"relativePath":"CL/pyconv.md","lastUpdated":1675251710000}'),n={name:"CL/pyconv.md"},c=o(`<h1 id="pxt-pyconv-手册页" tabindex="-1">pxt-pyconv 手册页 <a class="header-anchor" href="#pxt-pyconv-手册页" aria-hidden="true">#</a></h1><p>将 MicroPython 模块转换为 PXT</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">pxt pyconv &lt;directory&gt; [&lt;support directory&gt;...]</span></span>
<span class="line"><span style="color:#A6ACCD;">pxt pyconv &lt;file.py&gt;... [&lt;support directory&gt;...]</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-hidden="true">#</a></h2><p>在<code>&lt;directory&gt;</code>中找到的所有模块都将被转换并写入当前目录。通过存在<code>setup.py</code>、<code>README.rst</code>或类似文件来检测模块。</p><p>如果指定一个或多个<code>.py</code>文件而不是目录，它们将被转换</p><p>将在支持目录中搜索其他 Python 模块。它们在后台进行转换，但不会写入结果。</p><h2 id="python-在哪里" tabindex="-1">python 在哪里? <a class="header-anchor" href="#python-在哪里" aria-hidden="true">#</a></h2><p>该命令要求 Windows 上的 <strong>python3</strong> 或 <code>python3</code> 可执行文件位于 <code>PATH</code> 中。或者可以在 <code>PYTHON3</code> 环境变量中指定 python 路径。</p><h2 id="局限性" tabindex="-1">局限性 <a class="header-anchor" href="#局限性" aria-hidden="true">#</a></h2><p>只支持 Python 的一小部分。输出是 PXT 模块的起点。</p><ul><li><code>__getitem__</code>, <code>__setitem__</code>不支持（索引器）</li><li>变量的范围可能会混淆-您可能需要手动拉出变量</li></ul><p><code>try/catch</code>是由 PXT 不支持的转换。</p><h2 id="另请参见" tabindex="-1">另请参见 <a class="header-anchor" href="#另请参见" aria-hidden="true">#</a></h2><p><a href="https://makecode.com/cli" target="_blank" rel="noreferrer">pxt</a> tool</p>`,15),p=[c];function s(r,l,d,i,h,_){return a(),t("div",null,p)}const g=e(n,[["render",s]]);export{u as __pageData,g as default};
