import{_ as e,c as a,o as t,a as r}from"./app.af31bdc2.js";const f=JSON.parse('{"title":"什么是PXT？","description":"","frontmatter":{},"headers":[{"level":2,"title":"主要特点","slug":"主要特点","link":"#主要特点","children":[]},{"level":2,"title":"MakeCode","slug":"makecode","link":"#makecode","children":[]},{"level":2,"title":"MakeCode和PXT","slug":"makecode和pxt","link":"#makecode和pxt","children":[]}],"relativePath":"guide/what-is-pxt.md","lastUpdated":1675251710000}'),o={name:"guide/what-is-pxt.md"},i=r('<h1 id="什么是pxt" tabindex="-1">什么是PXT？ <a class="header-anchor" href="#什么是pxt" aria-hidden="true">#</a></h1><p><a href="https://github.com/microsoft/pxt" target="_blank" rel="noreferrer">PXT</a> 全称为 Microsoft Programming Experience Toolkit（PXT）编程体验工具包。</p><p>是一个为初学者创建特殊目的编程体验的框架，尤其侧重于计算机科学教育。PXT 的底层编程语言是 TypeScript 的子集(忽略了 JavaScript 的动态特性)。</p><h2 id="主要特点" tabindex="-1">主要特点 <a class="header-anchor" href="#主要特点" aria-hidden="true">#</a></h2><ul><li>一个基于 <a href="https://github.com/google/blockly" target="_blank" rel="noreferrer">Blockly</a> 的代码编辑器以及文本格式转换器</li><li>类似于VS Code的代码编辑器</li><li>在 TypeScript 中定义新块的可扩展性支持</li><li>一个 ARM 拇指机器代码发射器</li><li>命令行包管理器</li></ul><h2 id="makecode" tabindex="-1">MakeCode <a class="header-anchor" href="#makecode" aria-hidden="true">#</a></h2><p><a href="https://makecode.com/about" target="_blank" rel="noreferrer">Microsoft MakeCode</a> 是一个用于创建JavaScript/积木块在线编辑器的框架，用于为编程世界的新手创建交互式和引人入胜的编程体验。该平台为定制编码体验奠定了基础，以便在实际硬件或模拟目标中创建和运行用户程序。</p><p><img src="https://pxt.azureedge.net/blob/14fe220545e0013de0f5aee769914e07f18bcfbc/static/about/editor-to-target.png" alt="图片"></p><p>使用 MakeCode 构建的编辑器示例：</p><ul><li><a href="https://makecode.microbit.org" target="_blank" rel="noreferrer">https://makecode.microbit.org</a></li><li><a href="https://arcade.makecode.com" target="_blank" rel="noreferrer">https://arcade.makecode.com</a></li><li><a href="https://makecode.adafruit.com" target="_blank" rel="noreferrer">https://makecode.adafruit.com</a></li><li><a href="https://minecraft.makecode.com" target="_blank" rel="noreferrer">https://minecraft.makecode.com</a></li><li><a href="https://makecode.mindstorms.com" target="_blank" rel="noreferrer">https://makecode.mindstorms.com</a></li><li><a href="https://makecode.chibitronics.com" target="_blank" rel="noreferrer">https://makecode.chibitronics.com</a></li><li>更多编辑器：<a href="https://makecode.com/labs" target="_blank" rel="noreferrer">https://makecode.com/labs</a></li></ul><h2 id="makecode和pxt" tabindex="-1">MakeCode和PXT <a class="header-anchor" href="#makecode和pxt" aria-hidden="true">#</a></h2><p>Microsoft MakeCode基于开源项目Microsoft Programming Experience Toolkit（PXT）。该项目作为公共存储库托管在 GitHub 上。</p>',12),c=[i];function l(d,s,n,h,p,m){return t(),a("div",null,c)}const _=e(o,[["render",l]]);export{f as __pageData,_ as default};
